import React from 'react';
import { Box, Anchor, Text } from 'grommet';
import { Previous } from 'grommet-icons';

const resultHeader = ({ 
  customSearchData, 
  toggleCustomForm, 
  location, 
  isCustom, 
  toggleUi 
}) => {
  return (
    <React.Fragment>
      {!isCustom && 
        <Box 
          pad={{ left: "small", right: "small" }}
          alignContent="center"
          background="light-1"
          justify="around"
          direction="row" 
          align="center"
          height="15%" 
        >
          <Box width="20%">       
            <Anchor 
              onClick={() => toggleUi('landing')}
              margin={{ top: "4px" }}
              icon={<Previous />}
              color="dark-1"
              size="small" 
            />
          </Box>
          <Box width="60%" align="center">
            <Text size="small" weight="bold">
              Eating in
            </Text>
            <Text size="large" weight="bold" color="status-warning">
              {location}
            </Text>
          </Box>
          <Box width="20%"></Box>
        </Box>
      }

      {isCustom && 
        <Box 
          pad={{ left: "small", right: "small" }}
          alignContent="center"
          background="light-1"
          justify="around"
          align="center"
          height="30%" 
        >
          {/* <ul>
            {customSearchData.searchTerms.map((el, index) => {
              return <li key={index}>{el}</li>
            })} 
          </ul>         
          <h6>Sorting by: {customSearchData.sortBy}</h6>
          <button onClick={() => toggleCustomForm()}>EDIT SEARCH CRITERIA</button> */}
          <Box height="50%" direction="row" width="100%" border={{ color: "brand" }}>
            <Box width="20%">       
              <Anchor 
                onClick={() => toggleUi('landing')}
                margin={{ top: "4px" }}
                icon={<Previous />}
                color="dark-1"
                size="small" 
              />
            </Box>
            <Box width="60%" align="center">
              <Text size="small" weight="bold">
                Eating in
              </Text>
              <Text size="large" weight="bold" color="status-warning">
                {location}
              </Text>
            </Box>
            <Box width="20%"></Box>
          </Box>


        </Box>
      }
    </React.Fragment>
  );
};

export default resultHeader;
