import React, { Component } from 'react';
import ListItem from './ListItem';

// TODAS LAS OPCIONES DAR LOS PRIMEROS 3 DEL ARRAY
// EN OPCION RANDOM SI SE DAN 3 RANDOM DE LA LISTA EN ACOMODO RANDOM

class CustomView extends Component {
  state = {
    data: [],
    randomPicks: [],
    searchTerms: [],
    currentTerm: '',
    sortBy:'best_match',
    distance: ''
  };

  // FUNCTION THAT CREATES 3 RANDOM NUMBERS WITH DATA ARRAY LENGTH
  getIndeces = (businesses) => {
    let indeces = [];

    // HANDLE IF DATA RETURNED IS LESS THAN 3
    if (businesses.length < 3) {

      businesses.forEach((el, index) => {
        indeces.push(index);
      });

    } else {

      while (indeces.length < 3) {
        let number = Math.floor(Math.random() * Math.floor(businesses.length));

        if (!indeces.includes(number)) {
          indeces.push(number);
        }
      };

    }

    return indeces;
  };

  // SETS 3 RANDOM PICKS AND ADDS THEM TO STATE
  randomPicks = () => {
    let results = this.getIndeces(this.state.data).map(el => {
      return this.state.data[el];
    });

    return results;
  };

  // GET REQUEST WITH TERM
  searchTerm = async e => {
    e.preventDefault();
    const response = await fetch('/search', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        term: this.state.searchTerms,
        radius: this.state.distance,
        sort_by: this.state.sortBy === 'random' ? this.randomSorting() : this.state.sortBy
      })
    });
    const businesses = await response.json();
    this.setState({ data: businesses.businesses });    
    this.setState({ randomPicks: this.randomPicks() });
  };


  // WHEN HITTING HIT SUBMIT
    // LOOP THROUGH ARRAY CONTAINING DIFFERENT TERMS


  // UPDATE STATE WITH FORM INPUTS
  handleInputChange = e => {
    const target = e.target;
    const name = target.name;

    if (target.type === 'number') {
      this.setState({ [name]: parseInt(target.value) });
    } else {
      this.setState({ [name]: target.value });
    }
  };


  randomSorting = () => {
    let criteria = ['best_match', 'rating', 'review_count', 'distance'];
    let index = Math.floor(Math.random() * Math.floor(criteria.length));
    return criteria[index];
  };

  addTerm = () => {
    if (this.state.searchTerms.length < 3) {
      let newTerms = [...this.state.searchTerms];
      newTerms.push(this.state.currentTerm);

      this.setState({ searchTerms: newTerms });
      this.setState({ currentTerm: '' });
    };
  };

  render() {
    return (
      <div>
        <h1>Custom View</h1>

        <button onClick={this.logState}>Console log component state</button>

        <form onSubmit={this.searchTerm}>
          <label>
            Name:
            <input 
              type="text" 
              name="currentTerm" 
              value={this.state.currentTerm} 
              onChange={this.handleInputChange} 
            />
            <button onClick={this.addTerm}>Add term</button>
          </label>

          {
            this.state.searchTerms.map((el) => {
              return <p>{el}</p>
            })
          }

          <label>
            Sort by:
            <select name="sortBy" value={this.state.sortBy} onChange={this.handleInputChange} >
              <option value="best_match">Best Match</option>
              <option value="rating">Rating</option>
              <option value="review_count">Review Count</option>
              <option value="distance">Distance</option>
              <option value="random">Random</option>
            </select>
          </label>

          <label>
            Distance en KMs:
            <input 
              name="distance" 
              type="number" 
              value={this.state.distance} 
              onChange={this.handleInputChange} 
            />
          </label>

          <input type="submit" value="Submit" />
        </form>

        {

          // IF SORT BY ESTA EN RANDOM => RETURN 3 RANDOM PICKS

          this.state.randomPicks.map((el, index) => {
            return <ListItem key={index} item={el} />
          })

          // SI NO, DAR LOS 3 PRIMEROS DEL ARRAY REGRESADO



        }

        <button onClick={() => console.log(this.state)}>Get state</button>
      </div>
    );
  };
};

export default CustomView;
