const resultList = ({ data, randomSorting }) => {
  const [businesses, setBusinesses] = useState([]);
  const [displayItems, setDisplayItems] = useState([]);


  //revisar si usar funcion use effect asi esta bien o no
  useEffect(() => {
    async function fetchData() {
      let newData = await data;
      //setInitialStates(newData);
      setBusinesses(newData);    
    }

    console.log('FETCHING DATA FROM PROPS USE EFFECT');
    fetchData();
  }, [businesses]);




  const setStateRandom = (inputData) => {
    let displayItems = [];
    let data = [];

    if (inputData.length <= 3) {
      displayItems = inputData;
    } else {
      displayItems = this.randomPicks(inputData);
      data = inputData.filter(el => displayItems.includes(el) === false);
    }

    this.setState({ data });
    this.setState({ displayItems });
    console.log(inputData);
  }

  const setStateCustom = (inputData) => {
    console.log(inputData);
  }


    // if (this.props.isCustom) {
    //   this.setStateCustom(this.props.data);
    // } else {
