    // }
    //this.setState({ data: this.props.data });











  // returns 3 random indeces depending on input array length
  const getIndeces = (businesses) => {
    let indeces = [];
    if (businesses.length < 3) {
      indeces.push(0, 1);
    } else {
      while (indeces.length < 3) {
        let number = Math.floor(Math.random() * Math.floor(businesses.length));
        if (!indeces.includes(number)) {
          indeces.push(number);
        }
      };
    }
    return indeces;
  }

  // returns 3 random array items using get indeces function
  const randomPicks = (array) => {
    let results = this.getIndeces(array).map(el => {
      return array[el];
    })
    return results;
  }


  const setInitialStates = (inputData) => {
    let displayItems = [];
    let data = [];

    if (inputData.length < 3) {
      displayItems = inputData;

    } else {

      if(randomSorting) {
        displayItems = randomPicks(inputData);

      } else {
        for (let i=0; i<3; i++) {
          displayItems.push(inputData[i]);
        }

      }
      data = inputData.filter(el => displayItems.includes(el) === false);

    }

    //displayItems = inputData;
    console.log('SETTING INITIAL STATES')
    setBusinesses(data);
    setDisplayItems(displayItems);
  }






  ///////// FUNCTION TO REMOVE ITEMS BY CLICKING EACH CARD AND UPDATE EACH STATE ///////////////////
