  //     newItem = newState[this.getIndeces(newState)[0]];
  //     // update new state, filtering out the new item
  //     newState = newState.filter(el => newItem.name !== el.name);
  //   } else {
  //     // save value of removed item from array with shift
  //     newItem = newState.shift();
  //   }

  //   if (newItem !== undefined) {
  //     newDisplayItems.push(newItem);
  //   }

  //   this.setState({ data: newState });
  //   this.setState({ displayItems: newDisplayItems });
  // }



  return (
    <div>
      <button onClick={() => console.log(businesses)}>GET BUSINESSES STATE</button>
      <button onClick={() => console.log(displayItems)}>GET DISPLAY ITEMS STATE</button>


        {/* {
          businesses.length === 0 ?
          // ADD NO BUSINESSES COMPONENT
          <h1>There are no businesses</h1> :
          // ELSE RETURN DISPLAY ITEMS
          businesses.map((el, index) => {
            // return <li key={index} onClick={() => this.removeItem(index)}>{el.name}</li>
            return <li key={index}>{el.name}</li>
          })
        } */}

        <ul>
          {

              // data[0].map((el, index) => {
              //   return <li key={index}>{el.name}</li>
              // })

          }
        </ul>
    </div>
  );
};

