import React, { Fragment } from 'react';

// updates state with pertaining custom search info
  // when user clicks submit, action is dispatched to fetch data from store


const customForm = () => {
  return (
    <Fragment>
      <h1>Custom Form</h1>

      <form onSubmit={this.fetchData}>
          <label>
            Name:
            <input 
              type="text" 
              name="currentTerm" 
              value={this.state.currentTerm} 
              onChange={this.handleInputChange} 
            />
            <button onClick={this.addTerm}>Add term</button>
          </label>

          {
            this.state.searchTerms.map((el, index) => {
              return <p key={index} onClick={() => this.removeTerm(index)} >{el}</p>
            })
          }

          <label>
            Sort by:
            <select name="sortBy" value={this.state.sortBy} onChange={this.handleInputChange} >
              <option value="best_match">Best Match</option>
              <option value="rating">Rating</option>
              <option value="review_count">Review Count</option>
              <option value="distance">Distance</option>
              <option value="random">Random</option>
            </select>
          </label>

          <input type="submit" value="Submit" />
        </form>
    </Fragment>
  );
};

export default customForm;
