import React, { useReducer, createContext } from "react";

const initialState = {
  customView: {
    resultsMode: false,
    searchTerms: [],
    sortBy: 'best_match'
  },
  data: []
}



const randomSorting = () => {
  let criteria = ['best_match', 'rating', 'review_count', 'distance'];
  let index = Math.floor(Math.random() * Math.floor(criteria.length));
  return criteria[index];
};


const searchTerm = async (term, sort_by) => {
  const response = await fetch('/search', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ 
      term,
      sort_by
    })
  });
  const businesses = await response.json();
  return businesses.businesses;
};


  const fetchData = (searchInputs) => {
    let sorting = searchInputs.sortBy === 'random' ? randomSorting() : searchInputs.sortBy
    let data = [];
    if (searchInputs.searchTerms.length === 0) {
      searchTerm(null, sorting).then(response => {
        data.push(response);
      });
    } else {      
      searchInputs.searchTerms.forEach(el => {
        searchTerm(el, sorting).then(response => {
          data.push(response);
        });
      });
    }
    return data;
  };


/////////////////////////////////////////////////
  const fetchRandomData = async () => {
    const response = await fetch('/getRandom');
    const businesses = await response.json();
    return businesses.businesses;
  };
/////////////////////////////////////////////////



function reducer(state, action) {
  let customViewCopy = {...state.customView};

  switch(action.type) {
    case 'TOGGLE':

      customViewCopy = state.customView
      customViewCopy.resultsMode = !customViewCopy.resultsMode;
      return { ...state, customView: customViewCopy};

    case 'SUBMIT_SEARCH':
      // UPDATE SEARCH TERMS IN STATE
      customViewCopy = {...state.customView}
      customViewCopy.searchTerms = action.payload.searchTerms; 
      customViewCopy.sortBy = action.payload.sortBy;
      customViewCopy.resultsMode = true;

      // FETCH DATA AND UPDATE STATE 
      let data = fetchData(action.payload);

      return {...state, customView: customViewCopy, data};

    case 'FETCH_RANDOM_DATA':
      let randomData = [];
      fetchRandomData().then(response => randomData.push(response));
      return {...state, data: randomData };

    default:
      return state;
  }
}

export function StoreProvider(props) {
  const [state, dispatch] = useReducer(reducer, initialState)
  const value = {state, dispatch}
  return <Store.Provider value={value}>{props.children}</Store.Provider>
}

export const Store = createContext();
