  // API GET REQUEST FETCHES LOCAL BUSINESSES FROM COORDINATES
  getJson = async e => {
    e.preventDefault();
    const response = await fetch('/getJson');
    const businesses = await response.json();
    this.setState({ data: businesses.businesses });
  };

  logState = (e) => {
    e.preventDefault();
    console.log('[ LOGGING STATE ]');
    console.log(this.state);
  };

  // GET REQUEST WITH TERM
  searchTerm = async e => {
    e.preventDefault();
    const response = await fetch('/search', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        term: this.state.term,
        sort_by: this.state.sortBy,
        radius: this.state.distance
      })
    });
    const businesses = await response.json();
    this.setState({ data: businesses.businesses });
  };

  // HANDLE INPUT CHANGE UPDATING STATE
  handleChange = e => {
    this.setState({ term: e.target.value });
  };

  // HANDLE TYPE OF SORTING
  sorting = e => {
    this.setState({ sortBy: e.target.value });
  };

  // HANDLE DISTANCE SETTING STATE
  setDistance = e => {
