import React from 'react';

const styles = {
  border: '1px solid black',
  margin: '12px',
  textAlign: 'center'
};

const ListItem = ({ item, removeItem }) => {
  let latitude = item.coordinates.latitude;
  let longitude = item.coordinates.longitude;

  return (
    <div style={styles}>
      <h5>{item.name}</h5>
      <h6>{item.distance / 1000} kms</h6>

      <h6>Llamar</h6>
      <a href={`https://www.google.com/maps/search/?api=1&query=${longitude},${latitude}`}>Direccion</a>


      <button onClick={() => removeItem()}>Delete Item</button>
    </div>
  );
};

export default ListItem;
