import React, { Component } from 'react';
import { Grommet, Box } from 'grommet';
import Request from 'request';

import MainView from './components/MainView';

class App extends Component {
  state = {
    location: '',
    fetchSuccess: ''
  };

  componentWillMount() {
    Request.get({ "url": "http://ip-api.com/json/" }, (error, response, body) => {
        if (error) {
          this.setState({ fetchSuccess: false });  
          return console.dir(error);    
        }
        if (JSON.parse(body).status === "fail") {
          this.setState({ fetchSuccess: false });
        } else {
          const location = JSON.parse(body).city;
          this.setState({ location, fetchSuccess: true });
        }
    });
  };

  render() {
    return (
      <Grommet>
        <Box width="100vw" height="100vh">
          <MainView location={this.state.location} fetchSuccess={this.state.fetchSuccess}/>
        </Box>
      </Grommet>
    );
  }
}
export default App;
