import React from 'react';

import LandingHeader from './LandingHeader';

const landingView = ({ 
  location, 
  validBtn, 
  toggleUi, 
  fetchSuccess, 
  locationInput, 
  toggleLocationInputView, 
}) => {
  return (
    <div className="landing">
      {locationInput && (
        <Box height="15%" background="light-1" justify="center">
          <Anchor 
            size="small" 
            color="dark-1"
            icon={<Previous />}
            margin={{ top: '4px' }}
            onClick={() => toggleLocationInputView(false)}
          />
        </Box>
      )}
      <LandingHeader 
        location={location} 
        fetchSuccess={fetchSuccess} 
        locationInput={locationInput}
      /> 


          <Box height="40vh" animation="slideUp">
            <Box
              align="center"
              justify="center"
              height={locationInput ? "65%" : "50%"}
              background={{ 
                'opacity': validBtn() ? 'strong' : 'medium', 
                'color': 'status-warning'
              }}
              onClick={validBtn() ? () => toggleUi('random') : null}
            >
              <Heading 
                level="3"
                size="small" 
                textAlign="center" 
                color={validBtn()  ? "" : "dark-6"}
                >
                FIND ME PLACES
              </Heading>
            </Box>
            <Box
              height="35%"
              align="center"
              justify="center"
              background={{
                'opacity': validBtn() ? 'strong' : 'medium',
                'color': 'accent-4'
              }}
              onClick={validBtn() ? () => toggleUi('custom') : null}
            >
              <Heading 
                level="3"
                size="small" 
                textAlign="center" 
                color={validBtn() ? "" : "dark-6"}
              >
                CUSTOM SEARCH
              </Heading>
            </Box>
            {!locationInput && (
              <Box 
                height="20%" 
                justify="center" 
                background="dark-1" 
                onClick={() => toggleLocationInputView(true)}
              >
                <Heading 
                  level="5" 
                  size="xsmall" 
                  margin="none" 
                  textAlign="center"
                  alignSelf="center"
                >
                  WRONG LOCATION? FIX IT HERE
                </Heading>
              </Box>
            )}
          </Box>
    </div>
  )
}

export default landingView;
