import React from 'react';

import { Box, Text, Heading, Button } from 'grommet';

const choiceView = ({ item, toggleUi }) => {
  return (
    <Box background="status-warning" width="100%" height="100%" align="center">
      <Box height="90%" border={{ color: "dark-1" }} width="100%">
        <Heading color="light-1">{item.name}</Heading>
      </Box>
      <Box height="10%" border={{ color: 'dark-1' }}>
        <Button 
          color="dark-1" 
          onClick={() => toggleUi('landing')} 
          label="Try again!" 
          primary
        />
      </Box>
    </Box>
  )
}

export default choiceView;
