import React, { useState, useEffect, useContext } from 'react';
import { Store } from '../Store';



// 1 TERM && cualquier criterio menos random
// RETURN LOS PRIMERO 3 DE ARRAY DE RESPUESTA

// 1 TERM && random
// RETURN RANDOM 3 ITEMS

// 2 TERM && cualquier criterio menos random
// RETURN EL PRIMERO DE 1 Y LOS PRIMEROS 2 DE OTRO (cual tiene 1 y cual 2 luego se define)

// 2 TERM && random
// RETURN RANDOM 1 DE 1 Y 2 RANDOM DE OTRO

// 3 TERM && cualquier criterio menos random
// RETURN el primero de cada term

// 3 TERM && random 
// RETURN y 


const resultList = ({ data, randomSorting, isCustom, toggleUi, customSearchTerms }) => {
  const [businesses, setBusinesses] = useState([]);
  const [displayItems, setDisplayItems] = useState([{ name: 'Waiting for items' }]);
  const { state } = useContext(Store);

  useEffect(() => {
    if (isCustom) {
      setStatesCustom(data);
    } else {
      setStatesFromArray(data);
    }
  }, [data, setBusinesses]);



  const getIndeces = (businesses) => {
    let indeces = [];
    if (businesses.length <= 3) {
      indeces.push(0, 2);
    } else if (businesses.length !== 0) {
      while (indeces.length < 3) {
        let number = Math.floor(Math.random() * Math.floor(businesses.length));
        if (!indeces.includes(number)) {
          indeces.push(number);
        }
      };
    }
    return indeces;
  }

  const randomPicks = (array) => {
    let results = getIndeces(array).map(el => {
      return array[el];
    })
    return results;
  }


  // IF IS IN CUSTOM MODE = refactor
  const setStatesCustom = async (inputData) => {
    const setData = async () => {
      let newData = await inputData;
      return newData;
    }

    setData().then(res => {
      if (customSearchTerms.searchTerms.length === 0) {
        console.log('[CUSTOM - NO TERMS]')
        console.log(res);
        setStatesFromArray(res);
      } else {
        console.log('[CUSTOM - WITH TERMS]')
        console.log(res);
        setStatesFromTerms(res);
      }
    });
  }

  const setStatesFromTerms = async (inputData) => {
    let newData = await inputData;

      // if search random, mezclar todos los result arrays en uno solo
      if (randomSorting) {
        // let result = [];

        // newData.forEach(async function (item) {
        //   result = await [...result, ...item]
        // });

        const iterate = async (input) => {
          let newData = await input
          let result = [];

          newData.forEach((item) => {
            result = [...result, ...item];
          })

        }

        //setStatesFromArray(iterate(newData));

        iterate(newData).then(res => console.log(res));

      }


  }


  const setStatesFromArray = async (inputData) => {
    let newData = await inputData;
    let newDisplayItems = [];

    if (newData.length < 3 && newData.length !== 0) {
      newDisplayItems = newData;
    } else if (newData.length > 3) {
      if (randomSorting) {
        console.log('[RANDOM SORTING]')
        newDisplayItems = randomPicks(newData);
      } else {
        console.log('[NOT RANDOM SORTING]')
        newDisplayItems = [newData[0], newData[1], newData[2]];
      }

      for (let i=0; i<newDisplayItems.length; i++) {
        let item = newData.indexOf(newDisplayItems[i]);
        if (item > -1) { newData.splice(item, 1) };
      }
    }

    setBusinesses(newData);
    setDisplayItems(newDisplayItems); 
  }







  const removeItem = (passedIndex) => {
    // update display items by deleting clicked item
    let newDisplayItems = displayItems.filter((el, index) => passedIndex !== index);
    // copy state
    let newState = businesses;
    let newItem;


    // if random mode is selected
    if (randomSorting) {
      // select random item from new state and save it to new item
      newItem = newState[getIndeces(newState)[0]];
      // update new state, filtering out the new item
      newState = newState.filter(el => newItem.name !== el.name);
    } else {
      // save value of removed item from array with shift
      newItem = newState.shift();
    }

    if (newItem !== undefined) {
      newDisplayItems.push(newItem);
    }

    setBusinesses(newState);
    setDisplayItems(newDisplayItems);
  }



  return (
    <div>
      <button onClick={() => console.log(businesses)}>GET BUSINESSES STATE</button>
      <button onClick={() => console.log(displayItems)}>GET DISPLAY ITEMS STATE</button>

      { displayItems.length !== 0 ?
        <ul>
          { 
            displayItems.map((el, index) => {
              return <li key={index} onClick={() => removeItem(index)}>{el.name}</li>
            })
          }
        </ul>
        :
        // add fetching data message in the begginning
        // if display items are gone, display button to take user to main page to try again
        <button onClick={() => toggleUi('landing')}>Try again!</button>
      }
      <button onClick={() => console.log(state)}>GET STATE</button>
    </div>
  );
};

export default resultList;
