import React, { useContext } from 'react';
import { Heading, Box } from 'grommet';

import LocationInput from './LocationInput';
import CustomView from './CustomView';
import RandomView from './RandomView';
import { Store } from '../Store';
import Landing from './Landing';

const mainView = ({ location, fetchSuccess }) => {
  const { state, dispatch } = useContext(Store);
  const toggleUi = (ui) => dispatch ({ type: 'TOGGLE_UI', payload: ui });
  const toggleCustomForm = () => dispatch({ type: 'TOGGLE_CUSTOM_FORM' });

  return (
    <React.Fragment>
      {state.ui === 'landing' && (
        <React.Fragment>
          <Box height="60vh">
            {/* if state.ui es cualquier otra cosa menos location, show landing
              else
              show location change input
              pasarle funcion de toggle ui a location input
            */}

            {state.ui !== "locationInput" ? 
              <Landing location={location} fetchSuccess={fetchSuccess} />
              :
              <LocationInput toggleUi={toggleUi} />
            }


            {/* <Landing location={location} fetchSuccess={fetchSuccess} /> */}
          </Box>
          <Box height="40vh" animation="slideUp">
            <Box
              onClick={fetchSuccess ? () => toggleUi('random') : null}
              background="status-warning"
              justify="center"
              align="center"
              height="50%"
            >
              <Heading textAlign="center" size="small" level="3">
                FIND ME PLACES
              </Heading>
            </Box>
            <Box
              onClick={fetchSuccess ? () => toggleUi('custom') : null}
              background="accent-4"
              justify="center"
              align="center"
              height="35%"
            >
              <Heading textAlign="center" size="small" level="3">
                CUSTOM SEARCH
              </Heading>
            </Box>
            <Box height="15%" justify="center" background="dark-1">
              <Heading 
                onClick={() => toggleUi('locationInput')}
                alignSelf="center"
                textAlign="center"
                size="xsmall" 
                margin="none" 
                level="6" 
              >
                WRONG LOCATION? FIX IT HERE
              </Heading>
            </Box>
          </Box>
        </React.Fragment>
      )}
      {state.ui === 'custom' && 
        <CustomView 
          toggleCustomForm={toggleCustomForm}
          customView={state.customView}
          location={location} 
          toggleUi={toggleUi}
          data={state.data}
        />
      } 
      {state.ui === 'random' && 
        <RandomView 
          location={location} 
          toggleUi={toggleUi} 
          data={state.data}
        />
      }
    </React.Fragment>
  );
};

export default mainView;
