import React, { useContext } from 'react';
import { Store } from '../Store';

const resultHeader = ({ location, isCustom }) => {
  const { state, dispatch } = useContext(Store);


  const changeMode = () => {
    return dispatch({
      type: 'TOGGLE',
      payload: 'toggle'
    })
  }

  return (
    <div>
      <h5>Searching for eating options in {location}</h5>
      <h1>{isCustom ? 'Custom ' : 'Random '} Results Page</h1>

      {
        isCustom && <button onClick={() => changeMode()}>EDIT SEARCH CRITERIA</button>
      }

    </div>
  );
};

export default resultHeader;
